import osimport PILfrom PIL import Imageexist = Trueiter = 0while (exist):    if (os.path.exists("E:\\code\\python\\archivBot\\pic\\" + "pic" + str(iter) + ".png")):        with Image.open("E:\\code\\python\\archivBot\\pic\\" + "pic" + str(iter) + ".png") as img:            W,H = img.size            name = img.filename        os.rename(name, "E:\\code\\python\\archivBot\\pic\\"+ str(W) + "x" + str(H) + "_pic" + str(iter) + ".png")        iter = iter + 1    else:        exist = Falseprint("done")